 // DEPENDENCIES
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { ExamResults } from '../../domain/school-management-system_db/exam-results';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../ExamResults.service.ts
 */

/*
 * SCHEMA DB ExamResults
 *
	{
		Average: {
			type: 'Number',
			required : true
		},
		FullScore: {
			type: 'Number',
			required : true
		},
		Name: {
			type: 'String',
			required : true
		},
		Place: {
			type: 'Number',
			required : true
		},
		Score: {
			type: 'Number',
			required : true
		},
		Subject: {
			type: 'String',
			required : true
		},
		//RELATIONS
		//EXTERNAL RELATIONS
		_Student: {
			type: Schema.ObjectId,
			ref : "ExamResults"
		},
		_Subjects: {
			type: Schema.ObjectId,
			ref : "ExamResults"
		},
	}
 *
 */
@Injectable()
export class ExamResultsBaseService {

    contextUrl: string = environment.endpoint + '/examresults';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * ExamResultsService.create
    *   @description CRUD ACTION create
    *
    */
    create(item: ExamResults): Observable<ExamResults> {
        return this.http
            .post<ExamResults>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * ExamResultsService.delete
    *   @description CRUD ACTION delete
    *   @param ObjectId id Id
    *
    */
    remove(id: string): Observable<void> {
        return this.http
            .delete<void>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * ExamResultsService.findBy_Student
    *   @description CRUD ACTION findBy_Student
    *   @param Objectid key Id of model to search for
    *
    */
    findBy_Student(id: string): Observable<ExamResults[]> {
        return this.http
            .get<ExamResults[]>(this.contextUrl + '/findBy_Student/' + id)
            .pipe(
                map(response => response)
            );
    }

    /**
    * ExamResultsService.findBy_Subjects
    *   @description CRUD ACTION findBy_Subjects
    *   @param Objectid key Id of model to search for
    *
    */
    findBy_Subjects(id: string): Observable<ExamResults[]> {
        return this.http
            .get<ExamResults[]>(this.contextUrl + '/findBy_Subjects/' + id)
            .pipe(
                map(response => response)
            );
    }

    /**
    * ExamResultsService.get
    *   @description CRUD ACTION get
    *   @param ObjectId id Id resource
    *
    */
    get(id: string): Observable<ExamResults> {
        return this.http
            .get<ExamResults>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * ExamResultsService.list
    *   @description CRUD ACTION list
    *
    */
    list(): Observable<ExamResults[]> {
        return this.http
            .get<ExamResults[]>(this.contextUrl)
            .pipe(map(data => data));
    }

    /**
    * ExamResultsService.update
    *   @description CRUD ACTION update
    *   @param ObjectId id Id
    *
    */
    update(item: ExamResults): Observable<ExamResults> {
        return this.http
            .post<ExamResults>(this.contextUrl + '/' + item._id, item)
            .pipe(map(data => data));
    }


    // Custom APIs

}
